mapper.select:
select * from(
	select  r.*,
	(select t.status from m_simcard t where r.imsi=t.imsi) status
	from tb_problem_card r
) t
where 1=1

	and selectDate between #{startDate,jdbcType=TIMESTAMP} and #{endDate,jdbcType=TIMESTAMP}


	and tsid=#{tsid,jdbcType=INTEGER}


	and imsi=#{imsi,jdbcType=BIGINT}


	and status=#{status,jdbcType=INTEGER}

order by count desc,selectDate desc
