mapper.select:
		select rts.* 
		from  m_ready_terminal_sim rts
		where 1=1
		  
         	and rts.tsid like #{tsid,jdbcType=INTEGER}  
        
          
         	and rts.imsi like  #{imsi,jdbcType=BIGINT}

mapper.delete:
    delete from m_ready_terminal_sim
    where id in(
		
		 #{i,jdbcType=INTEGER}
		
		)
