mapper.select:
select * from m_simcard
where id in

	#{item,jdbcType=INTEGER}

select r.* ,t.name packageName ,p.name simPoolName ,d.name departmentName ,o.OPERATORNAMECN operatorName ,ifnull(c.namecn,c.countryName) countryName ,ifnull(pr.namecn,r.provincecode) provinceName from m_simcard r left join m_simpackage t on r.packageid=t.id and t.status=0 left join m_simpool p on r.cpid=p.spid and p.ISACTIVE=1 left join m_department d on p.departmentId=d.id and d.status=0 left join m_operator o on r.OPERATORCODE=o.OPERATORCODE left join m_country c on r.countryCode = c.countryCode left join m_province pr on r.countryCode = pr.countryCode and r.provinceCode = pr.provinceCode where 1=1
and r.cpid=#{cpId,jdbcType=INTEGER}
and r.status=#{status,jdbcType=INTEGER}
and d.id=#{departmentId,jdbcType=INTEGER}
and r.imsi=#{imsi,jdbcType=BIGINT}
and r.countryCode=#{countryCode,jdbcType=INTEGER}
and r.operatorCode=#{operatorCode,jdbcType=INTEGER}
order by r.cpid,r.cpchannelid

select r.ID ,r.IMSI ,r.NUMBER ,r.ICCID ,r.PIN ,r.OPERATORCODE ,r.MCNUMBER ,r.PACKAGEID ,r.OFFPERIOD ,r.SUSTAINED ,r.SIMMEPROTOCOL ,r.STATUS ,r.CPSTATUS ,r.CPID ,r.CPCHANNELID ,r.COUNTRYCODE ,r.PROVINCECODE ,r.EXPIRYDATE ,r.INSERTDATE ,r.USEDVPN ,r.VPNIP ,r.VPNNAME ,r.VPNPASS ,r.SOFTTYPE ,r.KI ,r.OPC ,r.OP ,r.APN ,r.OPENDATE ,r.DEPARTMENTID ,r.GROUPPREF ,r.NOTE ,t.name packageName ,p.name simPoolName ,d.name departmentName ,o.OPERATORNAMECN operatorName ,ifnull(c.namecn,c.countryName) countryName ,ifnull(pr.namecn,r.provincecode) provinceName from m_simcard r left join m_simpackage t on r.packageid=t.id and t.status=0 left join m_simpool p on r.cpid=p.spid and p.ISACTIVE=1 left join m_department d on p.departmentId=d.id and d.status=0 left join m_operator o on r.OPERATORCODE=o.OPERATORCODE left join m_country c on r.countryCode = c.countryCode left join m_province pr on r.countryCode = pr.countryCode and r.provinceCode = pr.provinceCode where 1=1
and r.cpid=#{cpId,jdbcType=INTEGER}
and r.status=#{status,jdbcType=INTEGER}
and d.id=#{departmentId,jdbcType=INTEGER}
and r.imsi=#{imsi,jdbcType=BIGINT}
and r.countryCode=#{countryCode,jdbcType=INTEGER}
and r.operatorCode=#{operatorCode,jdbcType=INTEGER}
order by r.cpid,r.cpchannelid

mapper.update:
update m_simcard 
set status=2 
where id in (

	#{item,jdbcType=INTEGER}

)

update m_simcard
set status=2
where imsi=#{imsi,jdbcType=BIGINT}

update m_simcard
set status = #{lastStatus,jdbcType=INTEGER}
where imsi = #{imsi,jdbcType=BIGINT}
