mapper.select:
select r.*, d.name departmentName from m_simpool r left join m_department d on r.departmentId=d.id and d.status=0 where 1=1
and r.spid = #{spid,jdbcType=INTEGER}
and r.isActive = #{isActive,jdbcType=INTEGER}
and r.name like concat('%',#{name,jdbcType=VARCHAR},'%')
order by r.spid

mapper.update:
update m_simpool
set departmentId= #{departmentId,jdbcType=INTEGER}  
where id= #{id,jdbcType=INTEGER}
