mapper.select:
select c.id, c.name, c.loginName, c.departmentId, c.phonenumber, c.password, c.sex, c.status, d.name departmentName from m_customer c, m_department d where c.departmentid=d.id and d.status=0
and c.status =#{status,jdbcType=INTEGER}
and c.name=#{name,jdbcType=VARCHAR}
and c.loginName=#{loginName,jdbcType=VARCHAR}
and d.id=#{departmentId,jdbcType=INTEGER}
