mapper.select:
select r.id, r.tsid, r.mac, r.model, r.batch, r.sVersion, r.key, r.status, r.upLog, r.imei, r.activated, r.homeLocation, r.ssid, r.wifiPassword, r.licFix, r.usedVpn, r.usedSoft, r.departmentId, r.meid, r.saleType, r.resetWifi, r.androidVersion, t.name departmentName, ifnull(k.namecn,k.countryname) countryName from m_terminal r left join m_department t on r.departmentId=t.id and t.status=0 left join m_country k on k.countrycode=r.homeLocation where 1=1
and r.tsid = #{tsid,jdbcType=INTEGER}
and r.status = #{status,jdbcType=INTEGER}
and r.activated = #{activated,jdbcType=INTEGER}
order by r.tsid
