mapper.select:
select r.* ,ifnull(c.namecn,c.countryname) countryName ,d.name departmentName ,concat(o.operatornamecn,' (',o.operatorCode,')') operatorName ,(select sversion from m_terminal mt where r.tsid=mt.tsid) sversion from m_terminal_sim r left join m_country c on c.countrycode=r.vcountrycode left join m_terminal t on t.tsid=r.tsid left join m_department d on t.departmentId=d.id and d.status=0 left join m_simcard s on s.imsi=r.imsi left join m_operator o on o.operatorCode=s.operatorCode where 1=1
and r.imsi = #{imsi,jdbcType=BIGINT}
and r.tsid = #{tsid,jdbcType=INTEGER}
and d.id = #{departmentId,jdbcType=INTEGER}
order by r.date desc
